mapper.delete:
delete from chat_message_read
where message_id = #{messageId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}

mapper.insert:
insert into chat_message_read (message_id, user_id, read_at
  )
values (#{messageId,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, #{readAt,jdbcType=TIMESTAMP}
  )

mapper.update:
update chat_message_read
set read_at = #{readAt,jdbcType=TIMESTAMP}
where message_id = #{messageId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}

mapper.select:
select message_id, user_id, read_at
from chat_message_read
where message_id = #{messageId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}
